def count_total
  puts "Tu n'as que #{$twitter.count} journalistes dans ton Pokédex, ATTRAPE-LES TOUS!"
end

def number
  puts "Il y a #{$twitter.count{|x| x[/\d+/]}} journalistes qui ont un numéro"
end

def aude
  i = 0
  $twitter.each.grep(/aude/) do
    i = i + 1
  end
  $twitter.each.grep(/Aude/) do
    i = i + 1
  end
  puts "Il y a #{i} journalistes qui ont 'aude' dans leur nom. ça sert à rien, mais j'aime bien ce prénom!"
end

def count_maj_first
  puts "Il n'y a que #{$twitter.count { |x| x[/@\p{Upper}/] }} journalistes qui commencent par une majuscule !"
end

def count_maj_total
  i = 0
  $twitter.each do |word|
    if (word != word.downcase)
    i = i + 1
    end
  end
  puts "Il n'y a que #{i} journalistes qui possèdent des majuscules, bandes d'illettrés !"
end

def underscore
  i = 0
  $twitter.each.grep(/_/) do
    i = i + 1
  end
  puts "Il y a #{i} journalistes qui ont des ZunDeuRsKor, foutus millénials !"
end

def pokerap
  puts "Je prends une grande inspiration et... #{$twitter.sort.join(", ")}"
  puts "Attrapez-les tous, attrapez-les tous, JOURNALISTES !"
end

def fifty_shorter
  $twitter = $twitter.sort_by {|word| word.length}
  puts $twitter[1..50]
end

def find_him
  puts "Bruce Benamran, alias @epenser, est #{$twitter.index("@epenser")}ème"
end

def menu
  puts "- Le nombre de journalistes, tape 1"
  puts "- Lesquels contiennent un numéro, tape 2"
  puts "- Lesquels contiennent 'aude', tape 3"
  puts "- Lesquels commencent par une majuscule, tape 4"
  puts "- Lesquels contiennent une majuscule, tape 5"
  puts "- Combien il y a d'underscores, tape 6"
  puts "- La liste dans l'ordre alphabétique, tape 7"
  puts "- Les 50 noms les plus courts, tape 8"
  puts "- Le classement de @epenser, tape 9"
  print "> "
  input = gets.chomp.to_i
  if input == 1
    count_total
    retour
  elsif input == 2
    number
    retour
  elsif input == 3
    aude
    retour
  elsif input == 4
    count_maj_first
    retour
  elsif input == 5
    count_maj_total
    retour
  elsif input == 6
    underscore
    retour
  elsif input == 7
    pokerap
    retour
  elsif input == 8
    fifty_shorter
    retour
  elsif input == 9
    find_him
    retour
  else
    puts "Pardon je n'ai pas bien compris. Pour connaître..."
    menu
  end
end

def retour
  puts "Pour retourner au menu, tapez entrée."
  gets.chomp
    puts "Vous revoilà ! Pour connaître..."
    menu
end

def perform
  puts "Bienvenue chez OrwellCorp ! Pour connaître..."
  menu
end

perform